import { useState, useEffect, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import zoomPlugin from 'chartjs-plugin-zoom';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  zoomPlugin
);

function transformStockData(jsonData) {
  const institutions = Object.keys(jsonData);
  const dates = jsonData[institutions[0]].map(item => item.date);

  const datasets = institutions.map((inst, index) => ({
    label: `${inst} Price`,
    data: jsonData[inst].map(item => item.price),
    borderColor: [`#8884d8`, `#82ca9d`, `#ffc658`][index % 3],
    backgroundColor: [`#8884d8`, `#82ca9d`, `#ffc658`][index % 3],
    borderWidth: 2,
    tension: 0.1,
    fill: false
  }));

  return { dates, datasets };
}

function App() {
  const [chartData, setChartData] = useState({ dates: [], datasets: [] });
  const chartRef = useRef(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then(res => res.json())
      .then(jsonData => setChartData(transformStockData(jsonData)))
      .catch(error => console.error('Error:', error));
  }, []);

  const scrollChart = (direction) => {
    const chart = chartRef.current;
    if (!chart) return;

    const currentMin = chart.scales.x.min;
    const currentMax = chart.scales.x.max;
    const dataLength = chartData.dates.length;
    const windowSize = currentMax - currentMin;
    const scrollAmount = Math.round(windowSize * 0.2); // Scroll by 20% of visible window

    if (direction === 'left' && currentMin > 0) {
      chart.scales.x.min = Math.max(0, currentMin - scrollAmount);
      chart.scales.x.max = Math.max(windowSize, currentMax - scrollAmount);
    } else if (direction === 'right' && currentMax < dataLength - 1) {
      chart.scales.x.min = Math.min(currentMin + scrollAmount, dataLength - windowSize);
      chart.scales.x.max = Math.min(currentMax + scrollAmount, dataLength - 1);
    }

    chart.update();
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      zoom: {
        pan: {
          enabled: true,
          mode: 'x',
        },
        zoom: {
          wheel: {
            enabled: true,
          },
          pinch: {
            enabled: true
          },
          drag: {
            enabled: true,
            backgroundColor: 'rgba(0,0,0,0.1)',
            borderColor: 'rgba(0,0,0,0.3)',
            borderWidth: 1
          },
          mode: 'x',
        },
        limits: {
          x: {
            min: 'original',
            max: 'original',
            minRange: 30
          }
        }
      }
    },
    elements: {
      point: {
        radius: 0, // Hide points by default
        hoverRadius: 4 // Show points on hover
      },
      line: {
        borderWidth: 1 // Make lines thinner
      }
    },
    scales: {
      x: {
        border: {
          display: true,
          color: '#666'
        },
        grid: {
          display: true,
          color: '#ddd'
        },
        ticks: {
          maxRotation: 45,
          minRotation: 45
        },
        min: chartData.dates?.[0],
        max: chartData.dates?.[Math.min(365, chartData.dates.length - 1)]
      },
      y: {
        border: {
          display: true,
          color: '#666'
        },
        grid: {
          display: true,
          color: '#ddd'
        }
      }
    }
  };

  const data = {
    labels: chartData.dates,
    datasets: chartData.datasets
  };

  return (
    <div className="App">
      <h1>Stock Price Chart</h1>
      <div style={{ 
        height: '400px',
        padding: '20px',
        border: '1px solid #ddd',
        borderRadius: '8px',
        background: 'white'
      }}>
        <Line ref={chartRef} options={options} data={data} />
      </div>
      <div style={{ 
        marginTop: '10px',
        display: 'flex',
        justifyContent: 'center',
        gap: '10px'
      }}>
        <button 
          onClick={() => scrollChart('left')}
          style={{
            padding: '8px 16px',
            borderRadius: '4px',
            border: '1px solid #ddd',
            background: 'white',
            cursor: 'pointer'
          }}
        >
          ← Scroll Left
        </button>
        <button 
          onClick={() => chartRef.current?.resetZoom()}
          style={{
            padding: '8px 16px',
            borderRadius: '4px',
            border: '1px solid #ddd',
            background: 'white',
            cursor: 'pointer'
          }}
        >
          Reset View
        </button>
        <button 
          onClick={() => scrollChart('right')}
          style={{
            padding: '8px 16px',
            borderRadius: '4px',
            border: '1px solid #ddd',
            background: 'white',
            cursor: 'pointer'
          }}
        >
          Scroll Right →
        </button>
      </div>
      <div style={{
        marginTop: '10px',
        padding: '0 20px'
      }}>
        <input
          type="range"
          min="0"
          max={Math.max(0, chartData.dates.length - 1)}
          value={chartRef.current?.scales.x.min || 0}
          onChange={(e) => {
            const chart = chartRef.current;
            if (!chart) return;
            const newMin = parseInt(e.target.value);
            const windowSize = chart.scales.x.max - chart.scales.x.min;
            chart.scales.x.min = newMin;
            chart.scales.x.max = Math.min(newMin + windowSize, chartData.dates.length - 1);
            chart.update();
          }}
          style={{
            width: '100%',
            margin: '10px 0'
          }}
        />
      </div>
    </div>
  );
}

export default App;
