import { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function transformStockData(jsonData) {
  const institutions = Object.keys(jsonData);
  const dates = jsonData[institutions[0]].map(item => item.date);

  const datasets = institutions.map((inst, index) => ({
    label: `${inst} Price`,
    data: jsonData[inst].map(item => item.price),
    borderColor: [`#8884d8`, `#82ca9d`, `#ffc658`][index % 3],
    tension: 0.1
  }));

  return { dates, datasets };
}

function App() {
  const [chartData, setChartData] = useState({ dates: [], datasets: [] });

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then(res => res.json())
      .then(jsonData => setChartData(transformStockData(jsonData)))
      .catch(error => console.error('Error:', error));
  }, []);

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        ticks: {
          maxRotation: 45,
          minRotation: 45
        }
      }
    }
  };

  const data = {
    labels: chartData.dates,
    datasets: chartData.datasets
  };

  return (
    <div className="App">
      <h1>Stock Price Chart</h1>
      <div style={{ height: '400px' }}>
        <Line options={options} data={data} />
      </div>
    </div>
  );
}

export default App;
