import { useState, useEffect, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import zoomPlugin from 'chartjs-plugin-zoom';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  zoomPlugin
);

function transformStockData(jsonData) {
  const institutions = Object.keys(jsonData);
  const dates = jsonData[institutions[0]].map(item => item.date);

  const datasets = institutions.map((inst, index) => ({
    label: `${inst} Price`,
    data: jsonData[inst].map(item => item.price),
    borderColor: [`#8884d8`, `#82ca9d`, `#ffc658`][index % 3],
    backgroundColor: [`#8884d8`, `#82ca9d`, `#ffc658`][index % 3],
    borderWidth: 2,
    tension: 0.1,
    fill: false
  }));

  return { dates, datasets };
}

function App() {
  const [chartData, setChartData] = useState({ dates: [], datasets: [] });
  const chartRef = useRef(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then(res => res.json())
      .then(jsonData => setChartData(transformStockData(jsonData)))
      .catch(error => console.error('Error:', error));
  }, []);

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      zoom: {
        pan: {
          enabled: true,
          mode: 'x',
        },
        zoom: {
          wheel: {
            enabled: true,
          },
          pinch: {
            enabled: true
          },
          drag: {
            enabled: true,
            backgroundColor: 'rgba(0,0,0,0.1)',
            borderColor: 'rgba(0,0,0,0.3)',
            borderWidth: 1
          },
          mode: 'x',
        },
        limits: {
          x: {
            min: 'original',
            max: 'original',
            minRange: 30
          }
        }
      }
    },
    elements: {
      point: {
        radius: 2, // Hide points by default
        hoverRadius: 4 // Show points on hover
      },
      line: {
        borderWidth: 1 // Make lines thinner
      }
    },
    scales: {
      x: {
        border: {
          display: true,
          color: '#666'
        },
        grid: {
          display: true,
          color: '#ddd'
        },
        ticks: {
          maxRotation: 45,
          minRotation: 45
        },
        min: chartData.dates?.[0],
        max: chartData.dates?.[Math.min(365, chartData.dates.length - 1)]
      },
      y: {
        border: {
          display: true,
          color: '#666'
        },
        grid: {
          display: true,
          color: '#ddd'
        }
      }
    }
  };

  const data = {
    labels: chartData.dates,
    datasets: chartData.datasets
  };

  return (
    <div className="App">
      <h1>Stock Price Chart</h1>
      <div style={{ 
        height: '400px',
        padding: '20px',
        border: '1px solid #ddd',
        borderRadius: '8px',
        background: 'white'
      }}>
        <Line ref={chartRef} options={options} data={data} />
      </div>
      <div style={{ marginTop: '10px' }}>
        <button 
          onClick={() => chartRef.current?.resetZoom()}
          style={{
            padding: '8px 16px',
            borderRadius: '4px',
            border: '1px solid #ddd',
            background: 'white',
            cursor: 'pointer'
          }}
        >
          Reset View
        </button>
      </div>
    </div>
  );
}

export default App;
