import { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Zoom
} from 'chart.js';
import zoomPlugin from 'chartjs-plugin-zoom';

// Register ChartJS components and plugins
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  zoomPlugin
);

function transformStockData(jsonData) {
  const institutions = Object.keys(jsonData);
  const dates = jsonData[institutions[0]].map(item => item.date);

  const datasets = institutions.map((inst, index) => ({
    label: `${inst} Price`,
    data: jsonData[inst].map(item => item.price),
    borderColor: [`#8884d8`, `#82ca9d`, `#ffc658`][index % 3],
    backgroundColor: [`#8884d8`, `#82ca9d`, `#ffc658`][index % 3],
    borderWidth: 2,
    tension: 0.1,
    fill: false
  }));

  return { dates, datasets };
}

function App() {
  const [chartData, setChartData] = useState({ dates: [], datasets: [] });

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks')
      .then(res => res.json())
      .then(jsonData => setChartData(transformStockData(jsonData)))
      .catch(error => console.error('Error:', error));
  }, []);

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      zoom: {
        zoom: {
          wheel: {
            enabled: true,
          },
          pinch: {
            enabled: true
          },
          mode: 'xy',
        },
        pan: {
          enabled: true,
          mode: 'xy',
        }
      },
      title: {
        display: true,
        text: 'Stock Prices Over Time'
      }
    },
    scales: {
      x: {
        border: {
          display: true,
          color: '#666'
        },
        grid: {
          display: true,
          color: '#ddd'
        },
        ticks: {
          maxRotation: 45,
          minRotation: 45
        }
      },
      y: {
        border: {
          display: true,
          color: '#666'
        },
        grid: {
          display: true,
          color: '#ddd'
        }
      }
    }
  };

  const data = {
    labels: chartData.dates,
    datasets: chartData.datasets
  };

  return (
    <div className="App">
      <h1>Stock Price Chart</h1>
      <div style={{ 
        height: '400px',
        padding: '20px',
        border: '1px solid #ddd',
        borderRadius: '8px',
        background: 'white'
      }}>
        <Line options={options} data={data} />
      </div>
    </div>
  );
}

export default App;
